import React, { useState } from 'react';
import styled from 'styled-components';
import TextInputField from './TextInputField';
import DropdownField from './DropdownField';

const FieldContainer = styled.div`
  margin-bottom: 1rem;
  padding: 1rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  background: white;
`;

const Question = styled.div`
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 1rem;
`;

const Th = styled.th`
  padding: 0.5rem;
  border: 1px solid #ddd;
  background: #f5f5f5;
`;

const Td = styled.td`
  padding: 0.5rem;
  border: 1px solid #ddd;
`;

const AddRowButton = styled.button`
  margin-top: 1rem;
  padding: 0.5rem 1rem;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: #0056b3;
  }
`;

const TableField = ({ 
  question, 
  required, 
  columns = [], 
  value = [], 
  onChange,
  isPreview = false,
  error
}) => {
  const [rows, setRows] = useState(value || []);

  const handleAddRow = () => {
    const newRow = columns.reduce((acc, col) => {
      acc[col.id] = '';
      return acc;
    }, {});
    const newRows = [...rows, newRow];
    setRows(newRows);
    if (onChange) {
      onChange(newRows);
    }
  };

  const handleCellChange = (rowIndex, columnId, newValue) => {
    const newRows = rows.map((row, index) => {
      if (index === rowIndex) {
        return { ...row, [columnId]: newValue };
      }
      return row;
    });
    setRows(newRows);
    if (onChange) {
      onChange(newRows);
    }
  };

  return (
    <FieldContainer>
      <Question>
        {question}
        {required && <span style={{ color: 'red' }}> *</span>}
      </Question>
      <Table>
        <thead>
          <tr>
            {columns.map((column) => (
              <Th key={column.id}>{column.name}</Th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {columns.map((column) => (
                <Td key={column.id}>
                  {column.type === 'text' ? (
                    <input
                      type="text"
                      value={row[column.id] || ''}
                      onChange={(e) => handleCellChange(rowIndex, column.id, e.target.value)}
                      disabled={!isPreview}
                    />
                  ) : (
                    <select
                      value={row[column.id] || ''}
                      onChange={(e) => handleCellChange(rowIndex, column.id, e.target.value)}
                      disabled={!isPreview}
                    >
                      <option value="">Select an option</option>
                      {column.options?.map((option, index) => (
                        <option key={index} value={option.value}>
                          {option.value}
                        </option>
                      ))}
                    </select>
                  )}
                </Td>
              ))}
            </tr>
          ))}
        </tbody>
      </Table>
      {isPreview && (
        <AddRowButton onClick={handleAddRow} disabled={columns.length === 0}>
          Add Row
        </AddRowButton>
      )}
      {error && <div style={{ color: 'red', fontSize: '0.8rem', marginTop: '0.5rem' }}>{error}</div>}
    </FieldContainer>
  );
};

export default TableField;
