  },
  { 
    id: 'pastel', 
    name: 'Pastel Purple',
    primaryColor: '#a78bfa',
    icon: 'ðŸŸ£'
  },
  { 
    id: 'nature', 
    name: 'Nature Green',
    primaryColor: '#059669',
    icon: 'ðŸŸ¢'
  }
];

const ThemeSelector = ({ onThemeSelect, currentTheme }) => (
  <div>
    <h2 style={{ 
      fontSize: '1.125rem',
      fontWeight: '600',
      marginBottom: '1rem',
      display: 'flex',
      alignItems: 'center'
    }}>
      Form Theme
      <span style={{ 
        fontSize: '0.75rem', 
        color: '#6b7280',
        fontStyle: 'italic',
        fontWeight: 'normal',
        marginLeft: '0.5rem'
      }}>
        (Click to Apply)
      </span>
    </h2>

    <div className="card">
      <div style={{ display: 'flex', flexDirection: 'column' }}>
        {themes.map(theme => (
          <div
            key={theme.id}
            className="btn margin-responsive"
            onClick={() => onThemeSelect(theme.id)}
            style={{
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              marginBottom: '0.75rem',
              backgroundColor: theme.primaryColor,
              color: 'white',
              border: currentTheme === theme.id ? '2px solid white' : 'none',
              boxShadow: currentTheme === theme.id ? '0 0 0 2px #000' : 'none'
            }}
          >
            <span>{theme.icon}</span>
            <span>{theme.name}</span>
          </div>
        ))}
      </div>
    </div>
  </div>
);

export default ThemeSelector;
