import React, { useState, useEffect } from 'react';
import FieldRenderer from '../shared/FieldRenderer';
import { useFormSubmission } from '../../hooks/useFormSubmission';

const FormCanvas = ({ fields, isPreview, onFieldSelect, onFieldUpdate, onFieldDelete, formId }) => {
  const [formData, setFormData] = useState({});
  const { submitForm, submitted, loading, error } = useFormSubmission();

  // Initialize form data when fields change
  useEffect(() => {
    console.log("Fields changed in FormCanvas:", fields);
    const initialData = {};
    fields.forEach(field => {
      initialData[field.id] = field.value || '';
    });
    setFormData(initialData);
  }, [fields]);

  const handleFieldChange = (fieldId, value) => {
    console.log(`Field ${fieldId} changed to:`, value);

    // Update local state
    setFormData(prev => ({
      ...prev,
      [fieldId]: value
    }));

    // Update parent state if needed
    if (onFieldUpdate) {
      const fieldToUpdate = fields.find(f => f.id === fieldId);
      if (fieldToUpdate) {
        onFieldUpdate({
          ...fieldToUpdate,
          value: value
        });
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted with data:", formData);

    if (isPreview && formId) {
      try {
        await submitForm(formId, formData);
      } catch (err) {
        console.error("Error submitting form:", err);
      }
    }
  };

  // Display submission confirmation
  if (submitted) {
    return (
      <div className="card">
        <div className="text-center padding-responsive">
          <h2>Form Submitted Successfully!</h2>
          <p>Thank you for your submission.</p>
          <button 
            type="button"
            className="btn btn-primary"
            onClick={() => window.location.reload()}
          >
            Submit Another Response
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      {fields.length === 0 ? (
        <div className="text-center padding-responsive">
          <p>Drag fields here to build your form or use the controls to add fields.</p>
        </div>
      ) : (
        <form className="space-responsive" onSubmit={handleSubmit}>
          {fields.map((field) => (
            <div
              key={field.id}
              className={`padding-responsive margin-responsive ${isPreview ? '' : 'border border-dashed cursor-pointer'}`}
              onClick={!isPreview ? () => onFieldSelect(field) : undefined}
            >
              {!isPreview && (
                <div 
                  style={{
                    borderBottom: '1px solid #e5e7eb',
                    paddingBottom: '0.5rem',
                    marginBottom: '0.5rem',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center'
                  }}
                >
                  <div 
                    onClick={(e) => {
                      e.stopPropagation();
                      onFieldSelect(field);
                    }}
                    style={{
                      fontWeight: 'bold',
                      cursor: 'pointer',
                      textDecoration: 'underline',
                      color: '#2563eb'
                    }}
                  >
                    {field.type.charAt(0).toUpperCase() + field.type.slice(1)} Field
                  </div>
                  <span style={{ fontSize: '0.8rem', color: '#6b7280' }}>
                    Click to configure
                  </span>
                </div>
              )}

              <FieldRenderer
                field={field}
                isPreview={isPreview}
                onChange={(value) => handleFieldChange(field.id, value)}
              />

              {!isPreview && (
                <div className="flex justify-end margin-responsive" style={{ display: 'flex', justifyContent: 'flex-end', gap: '8px', marginTop: '0.5rem' }}>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={(e) => {
                      e.stopPropagation();
                      const fieldIndex = fields.findIndex(f => f.id === field.id);
                      if (fieldIndex > 0) {
                        // Create new array for immutability
                        const newFields = [...fields];
                        // Swap current field with the one above it
                        [newFields[fieldIndex], newFields[fieldIndex - 1]] = 
                        [newFields[fieldIndex - 1], newFields[fieldIndex]];
                        // Update the fields
                        onFieldUpdate(newFields);
                      }
                    }}
                  >
                    ↑
                  </button>

                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={(e) => {
                      e.stopPropagation();
                      const fieldIndex = fields.findIndex(f => f.id === field.id);
                      if (fieldIndex < fields.length - 1) {
                        // Create new array for immutability
                        const newFields = [...fields];
                        // Swap current field with the one below it
                        [newFields[fieldIndex], newFields[fieldIndex + 1]] = 
                        [newFields[fieldIndex + 1], newFields[fieldIndex]];
                        // Update the fields
                        onFieldUpdate(newFields);
                      }
                    }}
                  >
                    ↓
                  </button>

                  <button
                    type="button"
                    className="btn btn-secondary"
                    style={{ backgroundColor: '#f87171' }}
                    onClick={(e) => {
                      e.stopPropagation();
                      onFieldDelete(field.id);
                    }}
                  >
                    Delete
                  </button>
                </div>
              )}
            </div>
          ))}

          {isPreview && (
            <div className="text-center padding-responsive">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Form'}
              </button>
              {error && <div className="form-error">{error}</div>}
            </div>
          )}
        </form>
      )}
    </div>
  );
};

export default FormCanvas;
