import React, { useState, useRef } from 'react';
import { useApi } from '../../hooks/useApi';
import { useFormState } from '../../hooks/useFormState';
import FieldPalette from './FieldPalette';
import FormCanvas from './FormCanvas';
import FieldConfigPanel from './FieldConfigPanel';

const FormBuilderPage = () => {
  // Add a ref for the field configuration section
  const configSectionRef = useRef(null);

  const [fields, setFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);
  const [isPreview, setIsPreview] = useState(false);
  const { loading, error, post } = useApi();
  const { formValues, handleInputChange } = useFormState({
    title: 'Untitled Form',
    description: '',
  });

  const handleAddField = (fieldType) => {
    console.log("Adding field of type:", fieldType);

    // Create a new field based on type
    const newField = {
      id: Date.now().toString(),
      type: fieldType,
      question: `New ${fieldType} question`,
      isRequired: false,
    };

    // Add type-specific properties
    if (fieldType === 'dropdown') {
      newField.options = [
        { value: 'Option 1', label: 'Option 1' },
        { value: 'Option 2', label: 'Option 2' }
      ];
    } else if (fieldType === 'table') {
      newField.columns = [
        { name: 'Column 1', type: 'text' },
        { name: 'Column 2', type: 'text' }
      ];
    } else if (fieldType === 'text') {
      newField.minLength = 0;
      newField.maxLength = 100;
    }

    // Add field to state
    setFields(prevFields => [...prevFields, newField]);
    setSelectedField(newField);

    console.log("Field added:", newField);
  };

  const handleFieldSelection = (field) => {
    console.log("Selected field:", field);
    setSelectedField(field);

    // Scroll to the config section when a field is selected
    if (configSectionRef.current) {
      configSectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleFieldUpdate = (updatedField) => {
    console.log("Updating field:", updatedField);

    // Check if we're updating a single field or the entire fields array
    if (Array.isArray(updatedField)) {
      setFields(updatedField);
    } else {
      setFields(prevFields => 
        prevFields.map(field => 
          field.id === updatedField.id ? updatedField : field
        )
      );

      // Update selected field if it's the one being edited
      if (selectedField && selectedField.id === updatedField.id) {
        setSelectedField(updatedField);
      }
    }
  };

  const handleFieldDelete = (fieldId) => {
    console.log("Deleting field:", fieldId);

    setFields(prevFields => prevFields.filter(field => field.id !== fieldId));

    // Clear selection if the deleted field was selected
    if (selectedField && selectedField.id === fieldId) {
      setSelectedField(null);
    }
  };

  const handleSaveForm = async () => {
    try {
      const formData = {
        ...formValues,
        fields,
      };

      console.log("Saving form:", formData);

      const result = await post('/forms', formData);
      alert('Form saved successfully!');
      return result;
    } catch (err) {
      console.error('Error saving form:', err);
    }
  };

  const togglePreview = () => {
    console.log("Toggling preview mode. Current state:", isPreview);
    setIsPreview(!isPreview);
  };

  // Function to scroll to configuration section
  const scrollToConfig = () => {
    if (configSectionRef.current) {
      configSectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="container">
      <header className="padding-responsive">
        <h1>{isPreview ? 'Form Preview' : 'Form Builder'}</h1>
        {!isPreview && selectedField && (
          <div 
            onClick={scrollToConfig} 
            style={{
              cursor: 'pointer',
              textDecoration: 'underline',
              color: '#2563eb',
              marginTop: '0.5rem',
              display: 'inline-block'
            }}
          >
            Jump to Field Configuration →
          </div>
        )}
        <div style={{ display: 'flex', gap: '1rem', margin: '1rem 0' }}>
          <input
            type="text"
            name="title"
            value={formValues.title}
            onChange={handleInputChange}
            className="input"
            placeholder="Form Title"
            style={{ flex: 1 }}
          />
          <button 
            className="btn btn-secondary"
            onClick={togglePreview}
          >
            {isPreview ? 'Edit Form' : 'Preview Form'}
          </button>
          <button 
            className="btn btn-primary"
            onClick={handleSaveForm}
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Form'}
          </button>
        </div>
        {error && <div className="form-error">{error}</div>}
      </header>

      <main style={{ display: 'flex', gap: '1rem' }}>
        {!isPreview && (
          <aside className="padding-responsive" style={{ width: '250px' }}>
            <h2>Field Types</h2>
            <FieldPalette onFieldSelect={handleAddField} />
          </aside>
        )}

        <section className="padding-responsive" style={{ flex: 1 }}>
          <FormCanvas
            fields={fields}
            isPreview={isPreview}
            onFieldSelect={handleFieldSelection}
            onFieldUpdate={handleFieldUpdate}
            onFieldDelete={handleFieldDelete}
            formId="preview"
          />
        </section>

        {!isPreview && selectedField && (
          <aside 
            className="padding-responsive" 
            style={{ width: '300px' }}
            ref={configSectionRef} // Add the ref here
            id="field-configuration" // Add an ID for direct linking
          >
            <h2>Field Configuration</h2>
            <FieldConfigPanel 
              field={selectedField} 
              onFieldUpdate={handleFieldUpdate} 
            />
          </aside>
        )}
      </main>
    </div>
  );
};

export default FormBuilderPage;
