  { 
    id: 'dark', 
    name: 'Dark Mode',
    primaryColor: '#1f2937',
    secondaryColor: '#374151',
    buttonTextColor: '#f9fafb',
    icon: 'âš«'
  }
];

const ThemeSelector = ({ onThemeSelect, currentTheme }) => (
  <div>
    <h2 style={{ 
      fontSize: '1.125rem',
      fontWeight: '600',
      marginBottom: '1rem',
      display: 'flex',
      alignItems: 'center'
    }}>
      Form Theme
      <span style={{ 
        fontSize: '0.75rem', 
        color: '#6b7280',
        fontStyle: 'italic',
        fontWeight: 'normal',
        marginLeft: '0.5rem'
      }}>
        (Click to Apply)
      </span>
    </h2>

    <div className="card">
      <div style={{ display: 'flex', flexDirection: 'column' }}>
        {themes.map(theme => {
          const isDarkTheme = theme.id === 'dark';
          const buttonStyle = {
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            marginBottom: '0.75rem',
            backgroundColor: theme.primaryColor,
            color: isDarkTheme ? theme.buttonTextColor : 'white',
            border: currentTheme === theme.id ? '2px solid white' : 'none',
            boxShadow: currentTheme === theme.id 
              ? isDarkTheme 
                ? '0 0 0 2px #4b5563' 
                : '0 0 0 2px #000' 
              : 'none',
            borderRadius: '0.375rem',
            padding: '0.5rem 1rem',
          };

          return (
            <div
              key={theme.id}
              className="btn margin-responsive"
              onClick={() => onThemeSelect(theme.id)}
              style={buttonStyle}
            >
              <span>{theme.icon}</span>
              <span>{theme.name}</span>
            </div>
          );
        })}
      </div>
    </div>
  </div>
);

export default ThemeSelector;
