import React from 'react';

const TextInputField = ({ 
  question, 
  required, 
  value, 
  onChange, 
  isPreview,
  minLength,
  maxLength
}) => {
  return (
    <div className="margin-responsive">
      <label className="form-label">
        {question}
        {required && <span style={{ color: 'red' }}> *</span>}
      </label>
      <input
        type="text"
        value={value || ''}
        onChange={(e) => onChange(e.target.value)}
        disabled={!isPreview}
        className="input"
        minLength={minLength}
        maxLength={maxLength}
      />
    </div>
  );
};

export default TextInputField;
