import React from 'react';

const FieldConfigPanel = ({ field, onFieldUpdate }) => {
  if (!field) return null;

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    console.log(`Field property ${name} changed:`, type === 'checkbox' ? checked : value);

    onFieldUpdate({
      ...field,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleAddOption = () => {
    const newOptions = [...(field.options || [])];
    newOptions.push({ value: `Option ${newOptions.length + 1}`, label: `Option ${newOptions.length + 1}` });

    onFieldUpdate({
      ...field,
      options: newOptions,
    });
  };

  const handleUpdateOption = (index, value) => {
    const updatedOptions = [...field.options];
    updatedOptions[index] = { 
      ...updatedOptions[index], 
      value: value,
      label: value 
    };

    onFieldUpdate({
      ...field,
      options: updatedOptions,
    });
  };

  const handleRemoveOption = (index) => {
    onFieldUpdate({
      ...field,
      options: field.options.filter((_, i) => i !== index),
    });
  };

  const handleAddColumn = () => {
    const newColumns = [...(field.columns || [])];
    newColumns.push({ name: `Column ${newColumns.length + 1}`, type: 'text' });

    onFieldUpdate({
      ...field,
      columns: newColumns,
    });
  };

  const handleUpdateColumn = (index, updates) => {
    const updatedColumns = [...field.columns];
    updatedColumns[index] = { ...updatedColumns[index], ...updates };

    onFieldUpdate({
      ...field,
      columns: updatedColumns,
    });
  };

  const handleRemoveColumn = (index) => {
    onFieldUpdate({
      ...field,
      columns: field.columns.filter((_, i) => i !== index),
    });
  };

  return (
    <div className="field-config-content">
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        gap: '1.25rem',
      }}>
        <div>
          <label className="form-label" style={{ 
            marginBottom: '0.5rem', 
            fontWeight: '500',
            color: '#1f2937'
          }}>
            Question
          </label>
          <input
            type="text"
            name="question"
            value={field.question || ''}
            onChange={handleChange}
            className="input"
            style={{ padding: '0.625rem' }}
          />
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
          <label className="form-label" style={{ 
            marginBottom: 0, 
            fontWeight: '500',
            color: '#1f2937'
          }}>
            Required
          </label>
          <input
            type="checkbox"
            name="isRequired"
            checked={field.isRequired || false}
            onChange={handleChange}
          />
        </div>

        {field.type === 'text' && (
          <>
            <div>
              <label className="form-label" style={{ 
                marginBottom: '0.5rem',
                fontWeight: '500',
                color: '#1f2937'
              }}>
                Minimum Length
              </label>
              <input
                type="number"
                name="minLength"
                value={field.minLength || ''}
                onChange={handleChange}
                className="input"
                style={{ padding: '0.625rem' }}
              />
            </div>
            <div>
              <label className="form-label" style={{ 
                marginBottom: '0.5rem',
                fontWeight: '500',
                color: '#1f2937' 
              }}>
                Maximum Length
              </label>
              <input
                type="number"
                name="maxLength"
                value={field.maxLength || ''}
                onChange={handleChange}
                className="input"
                style={{ padding: '0.625rem' }}
              />
            </div>
          </>
        )}

        {field.type === 'dropdown' && (
          <div>
            <label className="form-label" style={{ 
              marginBottom: '0.75rem',
              fontWeight: '500',
              color: '#1f2937'
            }}>
              Options
            </label>
            {field.options?.map((option, index) => (
              <div key={index} style={{ 
                display: 'flex', 
                gap: '0.5rem', 
                marginBottom: '0.75rem',
                alignItems: 'center'
              }}>
                <input
                  type="text"
                  value={option.value || ''}
                  onChange={(e) => handleUpdateOption(index, e.target.value)}
                  className="input"
                  style={{ padding: '0.625rem' }}
                />
                <button
                  type="button"
                  className="btn btn-secondary"
                  style={{ 
                    backgroundColor: '#f87171', 
                    color: 'white',
                    padding: '0.375rem 0.5rem',
                    minWidth: 'auto'
                  }}
                  onClick={() => handleRemoveOption(index)}
                >
                  âœ•
                </button>
              </div>
            ))}
            <button
              type="button"
              className="btn btn-secondary"
              style={{ marginTop: '0.5rem' }}
              onClick={handleAddOption}
            >
              Add Option
            </button>
          </div>
        )}

        {field.type === 'table' && (
          <div>
            <label className="form-label">Columns</label>
            {field.columns?.map((column, index) => (
              <div key={index} className="card margin-responsive">
                <div>
                  <label className="form-label">Column Name</label>
                  <input
                    type="text"
                    value={column.name || ''}
                    onChange={(e) => handleUpdateColumn(index, { name: e.target.value })}
                    className="input"
                  />
                </div>
                <div style={{ marginTop: '0.5rem' }}>
                  <label className="form-label">Column Type</label>
                  <select
                    value={column.type || 'text'}
                    onChange={(e) => handleUpdateColumn(index, { type: e.target.value })}
                    className="input"
                  >
                    <option value="text">Text</option>
                    <option value="dropdown">Dropdown</option>
                  </select>
                </div>
                <button
                  type="button"
                  className="btn btn-secondary"
                  style={{ marginTop: '0.5rem', backgroundColor: '#f87171' }}
                  onClick={() => handleRemoveColumn(index)}
                >
                  Remove Column
                </button>
              </div>
            ))}
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleAddColumn}
            >
              Add Column
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FieldConfigPanel;
