import React, { useState } from 'react';

const FormValidationModal = ({ isOpen, onClose, formData, onSave }) => {
  const [validationCode, setValidationCode] = useState('1234');
  const [testValue, setTestValue] = useState('');
  const [testResult, setTestResult] = useState(null);

  if (!isOpen) return null;

  const handleTest = () => {
    if (!testValue) {
      setTestResult({
        passed: false,
        message: 'Please enter a test value'
      });
      return;
    }

    const passed = testValue === validationCode;
    setTestResult({
      passed,
      message: passed 
        ? 'Validation passed! You can save the form.' 
        : 'Validation failed. Please check your code.'
    });
  };

  const handleSave = () => {
    if (!testResult || !testResult.passed) {
      const confirmed = window.confirm(
        'You have not successfully tested the validation code. Are you sure you want to save anyway?'
      );

      if (!confirmed) return;
    }

    // Add validation code to form data
    const updatedFormData = {
      ...formData,
      validationCode,
    };

    onSave(updatedFormData);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div 
        className="rounded-lg shadow-2xl w-full max-w-lg p-0 overflow-hidden"
        style={{
          background: 'linear-gradient(145deg, #f0f7ff 0%, #e6f0ff 100%)',
          border: '1px solid rgba(209, 213, 219, 0.3)',
          boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)'
        }}
      >
        {/* Header with richer gradient background */}
        <div 
          className="p-6 text-white"
          style={{
            background: 'linear-gradient(to right, #3b82f6, #4f46e5, #6366f1)',
            borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
          }}
        >
          <div className="flex justify-between items-center">
            <h2 className="text-xl font-semibold">Form Validation</h2>
            <button 
              onClick={onClose}
              className="text-white hover:text-blue-100 bg-white bg-opacity-20 rounded-full p-2 transition-colors"
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <line x1="18" y1="6" x2="6" y2="18"></line>
                <line x1="6" y1="6" x2="18" y2="18"></line>
              </svg>
            </button>
          </div>
          <p className="mt-2 text-blue-100 text-sm">
            Set a security code to protect your form
          </p>
        </div>

        {/* Content area with enhanced gradient */}
        <div className="p-6 space-y-6">
          <div 
            className="rounded-lg p-5 shadow-sm"
            style={{
              background: 'linear-gradient(to bottom right, #ffffff, #f3f9ff)',
              border: '1px solid #e5efff'
            }}
          >
            <p className="mb-4 text-gray-700">
              Set a validation code that administrators will need to enter before submitting the form.
            </p>

            <label className="block text-sm font-medium text-gray-700 mb-2">Validation Code</label>
            <input
              type="text"
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
              value={validationCode}
              onChange={(e) => setValidationCode(e.target.value)}
              placeholder="Enter validation code"
            />
            <p className="text-xs text-gray-500 mt-2">
              This code will be required to save the form
            </p>
          </div>

          <div 
            className="rounded-lg p-5 shadow-sm"
            style={{
              background: 'linear-gradient(135deg, #eef4ff 0%, #e0edff 100%)',
              border: '1px solid #cde1ff'
            }}
          >
            <h3 className="font-medium text-indigo-800 mb-3 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2">
                <polyline points="9 11 12 14 22 4"></polyline>
                <path d="M21 12v7a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11"></path>
              </svg>
              Test Validation
            </h3>
            <div className="flex gap-2 mb-2">
              <input
                type="text"
                className="flex-1 p-3 border border-indigo-200 bg-white rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
                value={testValue}
                onChange={(e) => setTestValue(e.target.value)}
                placeholder="Enter test code"
              />
              <button
                type="button"
                className="text-white px-4 py-2 rounded-lg transition-colors shadow-sm"
                style={{
                  background: 'linear-gradient(to right, #4f46e5, #6366f1)', 
                  borderBottom: '3px solid rgba(67, 56, 202, 0.7)'
                }}
                onClick={handleTest}
              >
                Test
              </button>
            </div>

            {testResult && (
              <div className={`mt-3 p-3 rounded-lg ${
                testResult.passed 
                  ? 'bg-green-50 text-green-700 border border-green-200' 
                  : 'bg-red-50 text-red-700 border border-red-200'
              } flex items-start`}>
                <svg 
                  xmlns="http://www.w3.org/2000/svg" 
                  width="20" 
                  height="20" 
                  viewBox="0 0 24 24" 
                  fill="none" 
                  stroke="currentColor" 
                  strokeWidth="2" 
                  strokeLinecap="round" 
                  strokeLinejoin="round" 
                  className="mr-2 mt-0.5"
                >
                  {testResult.passed ? (
                    <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                  ) : (
                    <circle cx="12" cy="12" r="10"></circle>
                  )}
                  {testResult.passed ? (
                    <polyline points="9 11 12 14 22 4"></polyline>
                  ) : (
                    <>
                      <line x1="12" y1="8" x2="12" y2="12"></line>
                      <line x1="12" y1="16" x2="12.01" y2="16"></line>
                    </>
                  )}
                </svg>
                {testResult.message}
              </div>
            )}
          </div>
        </div>

        {/* Footer with actions */}
        <div 
          className="p-4 flex justify-end space-x-3 border-t"
          style={{
            background: 'linear-gradient(to bottom, #f5f9ff, #edf2ff)',
            borderColor: '#e5efff'
          }}
        >
          <button
            className="px-4 py-2 border border-gray-300 bg-white rounded-lg text-gray-700 hover:bg-gray-50 shadow-sm transition-colors"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 text-white rounded-lg shadow-sm transition-all"
            style={{
              background: 'linear-gradient(to right, #3b82f6, #4f46e5)',
              borderBottom: '3px solid rgba(59, 130, 246, 0.5)'
            }}
            onClick={handleSave}
          >
            Save Form
          </button>
        </div>
      </div>
    </div>
  );
};

export default FormValidationModal;
