import React from 'react';
import { useDrag } from 'react-dnd';

// Define the field types
const fieldTypes = [
  { type: 'text', label: 'Text Input', icon: '📝' },
  { type: 'dropdown', label: 'Dropdown', icon: '▼' },
  { type: 'table', label: 'Table', icon: '📊' },
  { type: 'file', label: 'File Upload', icon: '📎' },
];

// Draggable field type component
const DraggableFieldType = ({ fieldType, label, icon }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'FIELD_TYPE',
    item: { type: fieldType },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      className="btn btn-primary margin-responsive"
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem',
      }}
    >
      <span>{icon}</span>
      {label}
    </div>
  );
};

const FieldPalette = ({ onFieldSelect }) => (
  <div className="card">
    <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
      {fieldTypes.map(field => (
        <div key={field.type}>
          <DraggableFieldType 
            fieldType={field.type} 
            label={field.label} 
            icon={field.icon}
          />
          <div 
            className="btn btn-secondary margin-responsive"
            style={{ width: '100%', marginTop: '0.25rem' }}
            onClick={() => onFieldSelect(field.type)}
          >
            Click to Add
          </div>
        </div>
      ))}
    </div>
    <div className="margin-responsive" style={{ marginTop: '1rem' }}>
      <p className="text-sm text-gray-600">
        <strong>Drag and drop</strong> or click to add fields to your form.
      </p>
    </div>
  </div>
);

export default FieldPalette;
