import React from 'react';
import { useDrag } from 'react-dnd';
import Button from '../ui/Button';

const fieldTypes = [
  { type: 'text', label: 'Text Input', icon: 'ðŸ“' },
  { type: 'dropdown', label: 'Dropdown', icon: 'â–¼' },
  { type: 'table', label: 'Table', icon: 'ðŸ“Š' },
  { type: 'file', label: 'File Upload', icon: 'ðŸ“Ž' },
];

const FieldType = ({ type, label }) => {
  const [, drag] = useDrag(() => ({
    type: 'field',
    item: { type },
  }));

  return (
    <div 
      ref={drag} 
      className="p-3 mb-2 border rounded cursor-move hover:bg-gray-50 transition-colors"
    >
      <Button variant="secondary" fullWidth>
        {label}
      </Button>
    </div>
  );
};

const FieldPalette = ({ onFieldSelect }) => (
  <div className="card">
    <div className="grid-cols-auto gap-responsive">
      {fieldTypes.map(field => (
        <button
          key={field.type}
          className="btn btn-primary margin-responsive"
          onClick={() => onFieldSelect(field.type)}
        >
          <span className="mr-2">{field.icon}</span>
          {field.label}
        </button>
      ))}
    </div>
  </div>
);

export default FieldPalette;
