import React, { useState } from 'react';
import styled from 'styled-components';
import TextInputField from './TextInputField';
import DropdownField from './DropdownField';

const FieldContainer = styled.div`
  margin-bottom: 1rem;
  padding: 1rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  background: white;
`;

const Question = styled.div`
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 1rem;
`;

const Th = styled.th`
  padding: 0.5rem;
  border: 1px solid #ddd;
  background: #f5f5f5;
`;

const Td = styled.td`
  padding: 0.5rem;
  border: 1px solid #ddd;
`;

const AddRowButton = styled.button`
  margin-top: 1rem;
  padding: 0.5rem 1rem;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: #0056b3;
  }
`;

const TableField = ({
  question,
  required,
  columns = [],
  value = [],
  onChange,
  isPreview
}) => {
  const [rows, setRows] = useState(value || []);

  const handleAddRow = () => {
    const newRow = columns.reduce((acc, col) => {
      acc[col.id || col.name] = '';
      return acc;
    }, {});
    const newRows = [...rows, newRow];
    setRows(newRows);
    if (onChange) {
      onChange(newRows);
    }
  };

  const handleCellChange = (rowIndex, columnKey, newValue) => {
    const newRows = rows.map((row, index) => {
      if (index === rowIndex) {
        return { ...row, [columnKey]: newValue };
      }
      return row;
    });
    setRows(newRows);
    if (onChange) {
      onChange(newRows);
    }
  };

  return (
    <FieldContainer>
      <Question>
        {question}
        {required && <span style={{ color: 'red' }}> *</span>}
      </Question>
      <Table>
        <thead>
          <tr>
            {columns.map((column, index) => (
              <Th key={index}>
                {column.name}
              </Th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {columns.map((column, colIndex) => {
                const columnKey = column.id || column.name;
                return (
                  <Td key={colIndex}>
                    {column.type === 'text' ? (
                      <input
                        type="text"
                        value={row[columnKey] || ''}
                        onChange={(e) => handleCellChange(rowIndex, columnKey, e.target.value)}
                        disabled={!isPreview}
                      />
                    ) : (
                      <select
                        value={row[columnKey] || ''}
                        onChange={(e) => handleCellChange(rowIndex, columnKey, e.target.value)}
                        disabled={!isPreview}
                      >
                        <option value="">Select</option>
                        {column.options?.map((option, optIndex) => (
                          <option key={optIndex} value={option.value}>
                            {option.value}
                          </option>
                        ))}
                      </select>
                    )}
                  </Td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </Table>
      {isPreview && (
        <AddRowButton onClick={handleAddRow} disabled={columns.length === 0}>
          Add Row
        </AddRowButton>
      )}
    </FieldContainer>
  );
};

export default TableField;
