import React from 'react';

const themes = [
  { 
    id: 'default', 
    name: 'Default', 
    colors: { primary: '#3b82f6', secondary: '#6b7280', accent: '#f59e0b' },
    preview: '#f0f9ff'
  },
  { 
    id: 'dark', 
    name: 'Dark Mode', 
    colors: { primary: '#2563eb', secondary: '#4b5563', accent: '#f59e0b' },
    preview: '#1f2937'
  },
  { 
    id: 'corporate', 
    name: 'Corporate', 
    colors: { primary: '#0ea5e9', secondary: '#64748b', accent: '#06b6d4' },
    preview: '#e0f2fe'
  },
  { 
    id: 'pastel', 
    name: 'Pastel', 
    colors: { primary: '#818cf8', secondary: '#a8a29e', accent: '#fb7185' },
    preview: '#f5f3ff'
  },
  { 
    id: 'nature', 
    name: 'Nature', 
    colors: { primary: '#059669', secondary: '#4b5563', accent: '#84cc16' },
    preview: '#ecfdf5'
  }
];

const ThemeSelector = ({ selectedTheme, onSelectTheme }) => {
  return (
    <div className="theme-selector">
      <h3 className="text-sm font-medium text-gray-700 mb-2">Form Theme</h3>
      <div className="grid grid-cols-2 gap-2">
        {themes.map((theme) => (
          <div
            key={theme.id}
            className={`theme-option p-2 rounded cursor-pointer transition-colors ${
              selectedTheme === theme.id
                ? 'ring-2 ring-offset-2 ring-blue-500'
                : 'border border-gray-200 hover:border-gray-300'
            }`}
            onClick={() => onSelectTheme(theme.id)}
            style={{ backgroundColor: theme.preview }}
          >
            <div className="flex justify-between items-center mb-2">
              <span className="text-xs font-medium">{theme.name}</span>
              {selectedTheme === theme.id && (
                <span className="text-blue-500 text-xs">âœ“</span>
              )}
            </div>
            <div className="flex space-x-1">
              <div
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: theme.colors.primary }}
              ></div>
              <div
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: theme.colors.secondary }}
              ></div>
              <div
                className="w-4 h-4 rounded-full"
                style={{ backgroundColor: theme.colors.accent }}
              ></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ThemeSelector;
