import React from 'react';
import { useDrag } from 'react-dnd';

// Define the field types
const fieldTypes = [
  { type: 'text', label: 'Text Input', icon: 'ðŸ“' },
  { type: 'dropdown', label: 'Dropdown', icon: 'â–¼' },
  { type: 'table', label: 'Table', icon: 'ðŸ“Š' },
  { type: 'file', label: 'File Upload', icon: 'ðŸ“Ž' },
];

// Combined draggable and clickable field type component
const DraggableFieldType = ({ fieldType, label, icon, onClick }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'FIELD_TYPE',
    item: { type: fieldType },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      className="btn btn-primary margin-responsive"
      onClick={() => onClick(fieldType)}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: 'grab',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0.75rem 1rem',
        transition: 'all 0.2s ease',
        marginBottom: '0.75rem',
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
        <span>{icon}</span>
        <span>{label}</span>
      </div>
      <div style={{ 
        fontSize: '0.7rem', 
        color: 'rgba(255,255,255,0.8)',
        border: '1px solid rgba(255,255,255,0.4)',
        borderRadius: '4px',
        padding: '2px 4px'
      }}>
        Drag or Click
      </div>
    </div>
  );
};

const FieldPalette = ({ onFieldSelect }) => (
  <div className="card">
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      {fieldTypes.map(field => (
        <DraggableFieldType 
          key={field.type}
          fieldType={field.type} 
          label={field.label} 
          icon={field.icon}
          onClick={onFieldSelect}
        />
      ))}
    </div>
    <div className="margin-responsive" style={{ marginTop: '1rem' }}>
      <p className="text-sm text-gray-600">
        <strong>Drag and drop</strong> or <strong>click</strong> to add fields to your form.
      </p>
    </div>
  </div>
);

export default FieldPalette;
