import React, { useState } from 'react';
import FieldRenderer from '../shared/FieldRenderer';
import { useFormSubmission } from '../../hooks/useFormSubmission';

const FormCanvas = ({ fields, isPreview, onFieldSelect, onFieldUpdate, onFieldDelete, formId }) => {
  const [formData, setFormData] = useState({});
  const { submitForm, submitted, loading, error } = useFormSubmission();

  const handleFieldChange = (fieldId, value) => {
    setFormData({
      ...formData,
      [fieldId]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isPreview && formId) {
      try {
        await submitForm(formId, formData);
      } catch (err) {
        // Error is handled by the hook
      }
    }
  };

  if (submitted) {
    return (
      <div className="card text-center padding-responsive">
        <h2>Form Submitted Successfully!</h2>
        <p>Thank you for your submission.</p>
      </div>
    );
  }

  return (
    <div className="card">
      {fields.length === 0 ? (
        <div className="text-center padding-responsive">
          <p>Drag fields here to build your form.</p>
        </div>
      ) : (
        <form className="space-responsive" onSubmit={handleSubmit}>
          {fields.map((field) => (
            <div
              key={field.id}
              className={`padding-responsive margin-responsive ${isPreview ? '' : 'border border-dashed cursor-pointer'}`}
              onClick={!isPreview ? () => onFieldSelect(field) : undefined}
            >
              <FieldRenderer
                field={field}
                isPreview={isPreview}
                onChange={(value) => {
                  if (isPreview) {
                    handleFieldChange(field.id, value);
                  } else {
                    onFieldUpdate({ ...field, value });
                  }
                }}
              />
              {!isPreview && (
                <div className="flex justify-end margin-responsive">
                  <button
                    type="button"
                    className="btn btn-secondary mr-2"
                    onClick={(e) => {
                      e.stopPropagation();
                      const position = fields.findIndex(f => f.id === field.id);
                      const newFields = [...fields];
                      if (position > 0) {
                        [newFields[position], newFields[position - 1]] = [newFields[position - 1], newFields[position]];
                        onFieldUpdate(newFields);
                      }
                    }}
                  >
                    â†‘
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary mr-2"
                    onClick={(e) => {
                      e.stopPropagation();
                      const position = fields.findIndex(f => f.id === field.id);
                      const newFields = [...fields];
                      if (position < fields.length - 1) {
                        [newFields[position], newFields[position + 1]] = [newFields[position + 1], newFields[position]];
                        onFieldUpdate(newFields);
                      }
                    }}
                  >
                    â†“
                  </button>
                  <button
                    type="button"
                    className="btn btn-error"
                    onClick={(e) => {
                      e.stopPropagation();
                      onFieldDelete(field.id);
                    }}
                  >
                    Delete
                  </button>
                </div>
              )}
            </div>
          ))}

          {isPreview && (
            <div className="text-center padding-responsive">
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Form'}
              </button>
              {error && <div className="form-error margin-responsive">{error}</div>}
            </div>
          )}
        </form>
      )}
    </div>
  );
};

export default FormCanvas;
