import React from 'react';

const FieldConfigPanel = ({ field, onFieldUpdate }) => {
  if (!field) return null;

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    console.log(`Field property ${name} changed:`, type === 'checkbox' ? checked : value);

    onFieldUpdate({
      ...field,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleAddOption = () => {
    const newOptions = [...(field.options || [])];
    newOptions.push({ value: `Option ${newOptions.length + 1}`, label: `Option ${newOptions.length + 1}` });

    onFieldUpdate({
      ...field,
      options: newOptions,
    });
  };

  const handleUpdateOption = (index, value) => {
    const updatedOptions = [...field.options];
    updatedOptions[index] = { 
      ...updatedOptions[index], 
      value: value,
      label: value 
    };

    onFieldUpdate({
      ...field,
      options: updatedOptions,
    });
  };

  const handleRemoveOption = (index) => {
    onFieldUpdate({
      ...field,
      options: field.options.filter((_, i) => i !== index),
    });
  };

  const handleAddColumn = () => {
    const newColumns = [...(field.columns || [])];
    newColumns.push({ name: `Column ${newColumns.length + 1}`, type: 'text' });

    onFieldUpdate({
      ...field,
      columns: newColumns,
    });
  };

  const handleUpdateColumn = (index, updates) => {
    const updatedColumns = [...field.columns];
    updatedColumns[index] = { ...updatedColumns[index], ...updates };

    onFieldUpdate({
      ...field,
      columns: updatedColumns,
    });
  };

  const handleRemoveColumn = (index) => {
    onFieldUpdate({
      ...field,
      columns: field.columns.filter((_, i) => i !== index),
    });
  };

  return (
    <div className="field-config-panel">
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column', 
        gap: '1.25rem' 
      }}>
        {/* Highlight the field type and question at the top */}
        <div style={{
          backgroundColor: '#f0f9ff', // Light blue background
          border: '1px solid #bae6fd',
          borderRadius: '0.375rem',
          padding: '0.75rem',
          marginBottom: '1rem'
        }}>
          <div style={{ 
            fontSize: '0.875rem', 
            color: '#0369a1', 
            fontWeight: 'bold', 
            marginBottom: '0.25rem' 
          }}>
            {field.type.charAt(0).toUpperCase() + field.type.slice(1)} Field
          </div>
          <div style={{ fontSize: '0.9375rem', fontWeight: '500' }}>
            {field.question || 'Untitled Question'}
          </div>
        </div>

        <div className="config-field">
          <label className="form-label">Question Text</label>
          <input
            type="text"
            name="question"
            value={field.question || ''}
            onChange={handleChange}
            className="input"
            placeholder="Enter question text"
          />
        </div>

        <div className="config-field">
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: '0.75rem' 
          }}>
            <input
              type="checkbox"
              id="isRequired"
              name="isRequired"
              checked={field.isRequired || false}
              onChange={handleChange}
              style={{ width: 'auto' }}
            />
            <label 
              htmlFor="isRequired" 
              className="form-label" 
              style={{ margin: 0, cursor: 'pointer' }}
            >
              Required Field
            </label>
          </div>
        </div>

        {field.type === 'text' && (
          <>
            <div className="config-field">
              <label className="form-label">Minimum Length</label>
              <input
                type="number"
                name="minLength"
                value={field.minLength || ''}
                onChange={handleChange}
                className="input"
                placeholder="0"
              />
            </div>
            <div className="config-field">
              <label className="form-label">Maximum Length</label>
              <input
                type="number"
                name="maxLength"
                value={field.maxLength || ''}
                onChange={handleChange}
                className="input"
                placeholder="100"
              />
            </div>
          </>
        )}

        {field.type === 'dropdown' && (
          <div className="config-field">
            <label className="form-label">Options</label>
            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '0.5rem', 
              marginTop: '0.5rem' 
            }}>
              {field.options?.map((option, index) => (
                <div key={index} style={{ 
                  display: 'flex', 
                  gap: '0.5rem' 
                }}>
                  <input
                    type="text"
                    value={option.value || ''}
                    onChange={(e) => handleUpdateOption(index, e.target.value)}
                    className="input"
                    placeholder={`Option ${index + 1}`}
                  />
                  <button
                    type="button"
                    className="btn btn-secondary"
                    style={{ 
                      backgroundColor: '#f87171', 
                      color: 'white',
                      padding: '0.25rem 0.5rem',
                      minWidth: '40px'
                    }}
                    onClick={() => handleRemoveOption(index)}
                  >
                    ✕
                  </button>
                </div>
              ))}
              <button
                type="button"
                className="btn btn-secondary"
                style={{ marginTop: '0.5rem' }}
                onClick={handleAddOption}
              >
                Add Option
              </button>
            </div>
          </div>
        )}

        {field.type === 'table' && (
          <div className="config-field">
            <label className="form-label">Table Configuration</label>

            {/* Table preview */}
            <div style={{ 
              backgroundColor: '#f9fafb',
              borderRadius: '0.375rem',
              padding: '0.5rem',
              marginBottom: '1rem',
              fontSize: '0.75rem',
              color: '#4b5563'
            }}>
              <div style={{ marginBottom: '0.5rem' }}>
                <strong>Note:</strong> Users will be able to add/remove rows when filling out the form.
              </div>
              {field.columns?.length > 0 ? (
                <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: '0.75rem' }}>
                  <thead>
                    <tr>
                      {field.columns.map((col, i) => (
                        <th key={i} style={{ 
                          border: '1px solid #d1d5db',
                          padding: '0.25rem',
                          backgroundColor: '#e5e7eb'
                        }}>
                          {col.name}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      {field.columns.map((col, i) => (
                        <td key={i} style={{ 
                          border: '1px solid #d1d5db',
                          padding: '0.25rem',
                          textAlign: 'center',
                          color: '#9ca3af'
                        }}>
                          [{col.type}]
                        </td>
                      ))}
                    </tr>
                  </tbody>
                </table>
              ) : (
                <div>Add columns to create your table structure.</div>
              )}
            </div>

            <label className="form-label">Table Columns</label>
            {field.columns?.map((column, index) => (
              <div key={index} className="card margin-responsive" style={{ padding: '0.75rem', marginBottom: '0.75rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                  <div style={{ fontWeight: '500' }}>Column {index + 1}</div>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    style={{ 
                      backgroundColor: '#f87171', 
                      color: 'white',
                      padding: '0.125rem 0.375rem',
                      fontSize: '0.75rem'
                    }}
                    onClick={() => handleRemoveColumn(index)}
                  >
                    Remove
                  </button>
                </div>

                <div style={{ marginBottom: '0.5rem' }}>
                  <label className="form-label">Column Name</label>
                  <input
                    type="text"
                    value={column.name || ''}
                    onChange={(e) => handleUpdateColumn(index, { name: e.target.value })}
                    className="input"
                    style={{ height: '2rem' }}
                  />
                </div>

                <div>
                  <label className="form-label">Column Type</label>
                  <select
                    value={column.type || 'text'}
                    onChange={(e) => handleUpdateColumn(index, { type: e.target.value })}
                    className="input"
                    style={{ height: '2rem' }}
                  >
                    <option value="text">Text</option>
                    <option value="dropdown">Dropdown</option>
                  </select>
                </div>

                {column.type === 'dropdown' && (
                  <div style={{ marginTop: '0.5rem' }}>
                    <label className="form-label">Dropdown Options</label>
                    {(column.options || []).map((option, optIndex) => (
                      <div key={optIndex} style={{ display: 'flex', gap: '0.25rem', marginBottom: '0.25rem' }}>
                        <input
                          type="text"
                          value={option.value || option}
                          onChange={(e) => {
                            const newOptions = [...(column.options || [])];
                            newOptions[optIndex] = e.target.value;
                            handleUpdateColumn(index, { options: newOptions });
                          }}
                          className="input"
                          style={{ height: '1.75rem' }}
                        />
                        <button
                          type="button"
                          className="btn"
                          style={{ 
                            padding: '0.25rem',
                            backgroundColor: '#f87171',
                            color: 'white'
                          }}
                          onClick={() => {
                            const newOptions = (column.options || []).filter((_, i) => i !== optIndex);
                            handleUpdateColumn(index, { options: newOptions });
                          }}
                        >
                          ✕
                        </button>
                      </div>
                    ))}
                    <button
                      type="button"
                      className="btn btn-secondary"
                      style={{ fontSize: '0.75rem', padding: '0.25rem 0.5rem' }}
                      onClick={() => {
                        const newOptions = [...(column.options || []), `Option ${(column.options || []).length + 1}`];
                        handleUpdateColumn(index, { options: newOptions });
                      }}
                    >
                      Add Option
                    </button>
                  </div>
                )}
              </div>
            ))}

            <button
              type="button"
              className="btn btn-primary"
              onClick={handleAddColumn}
            >
              Add Column
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FieldConfigPanel;
