import React, { useState, useEffect } from 'react';
import { useApi } from '../../hooks/useApi';
import { useFormState } from '../../hooks/useFormState';
import FieldPalette from './FieldPalette';
import FormCanvas from './FormCanvas';
import FieldConfigPanel from './FieldConfigPanel';

const FormBuilderPage = () => {
  const [fields, setFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);
  const [isPreview, setIsPreview] = useState(false);
  const { loading, error, post, get } = useApi();
  const { formValues, handleInputChange, setFieldValue } = useFormState({
    title: 'Untitled Form',
    description: '',
  });

  const handleAddField = (fieldType) => {
    const newField = {
      id: Date.now(),
      type: fieldType,
      question: `New ${fieldType} question`,
      isRequired: false,
      options: fieldType === 'dropdown' ? [{ value: 'Option 1' }] : [],
      columns: fieldType === 'table' ? [{ name: 'Column 1', type: 'text' }] : [],
    };
    setFields([...fields, newField]);
  };

  const handleFieldSelection = (field) => {
    setSelectedField(field);
  };

  const handleFieldUpdate = (updatedField) => {
    setFields(fields.map(field => field.id === updatedField.id ? updatedField : field));
    if (selectedField?.id === updatedField.id) {
      setSelectedField(updatedField);
    }
  };

  const handleFieldDelete = (fieldId) => {
    setFields(fields.filter(field => field.id !== fieldId));
    if (selectedField?.id === fieldId) {
      setSelectedField(null);
    }
  };

  const handleSaveForm = async () => {
    try {
      const formData = {
        ...formValues,
        fields,
      };
      const result = await post('/forms', formData);
      alert('Form saved successfully!');
      return result;
    } catch (err) {
      console.error('Error saving form:', err);
    }
  };

  const togglePreview = () => setIsPreview(!isPreview);

  return (
    <div className="container flex-col-auto">
      <header className="padding-responsive">
        <h1 className="text-responsive">{isPreview ? 'Form Preview' : 'Form Builder'}</h1>
        <div className="flex gap-responsive">
          <input
            type="text"
            name="title"
            value={formValues.title}
            onChange={handleInputChange}
            className="input"
            placeholder="Form Title"
          />
          <button 
            className="btn btn-secondary"
            onClick={togglePreview}
          >
            {isPreview ? 'Edit' : 'Preview'}
          </button>
          <button 
            className="btn btn-primary"
            onClick={handleSaveForm}
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Form'}
          </button>
        </div>
        {error && <div className="form-error">{error}</div>}
      </header>

      <main className="flex">
        {!isPreview && (
          <aside className="padding-responsive">
            <h2 className="text-responsive">Field Types</h2>
            <FieldPalette onFieldSelect={handleAddField} />
          </aside>
        )}

        <section className="padding-responsive flex-col-auto">
          <FormCanvas
            fields={fields}
            isPreview={isPreview}
            onFieldSelect={handleFieldSelection}
            onFieldUpdate={handleFieldUpdate}
            onFieldDelete={handleFieldDelete}
          />
        </section>

        {!isPreview && selectedField && (
          <aside className="padding-responsive">
            <h2 className="text-responsive">Field Configuration</h2>
            <FieldConfigPanel 
              field={selectedField} 
              onFieldUpdate={handleFieldUpdate} 
            />
          </aside>
        )}
      </main>
    </div>
  );
};

export default FormBuilderPage;
